globalStyles:
@import url('https://fonts.googleapis.com/css?family=Open+Sans|Roboto');

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

*:after, *:before {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: _default-font-stack;
  font-size: _bfs;
  letter-spacing: _default-letter-spacing;
  color: _black;
  background-color: _white;
}

h1, h2, h3, h4, h5, h6 {
  letter-spacing: _heading-letter-spacing;
  font-family: _heading-font-stack !important;
  margin-bottom: _vertical-rhythm;
  margin-top: 15px;
  color: _black;
}

h1 {
  font-size: _bfs-h1;
}

h2 {
  font-size: _bfs-h2;
}

h3 {
  font-size: _bfs-h3;
}

h4 {
  font-size: _bfs-h4;
}

h5 {
  font-size: _bfs-h5;
}

h6 {
  font-size: _bfs-h6;
}

.lead {
  font-size: _lead-font-size;
  letter-spacing: _lead-letter-spacing;
  text-align: _lead-alignment;
  color: _black;
}

p {
  line-height: _p-line-height;
  color: _black;
}

a {
  color: _black;
  text-decoration: none;
  transition: all .3s;
  cursor: pointer;
}

a:hover {
  letter-spacing: 5px;
}

label {
  font-weight: bold;
}

input[type="text"],
input[type="number"],
input[type="email"],
input[type="password"],
select {
  letter-spacing: _lead-letter-spacing;
  padding: 15px;
  border-radius: 5px;
  border: 1px solid _contrast-color;
  display: block;
  margin-bottom: _vertical-rhythm;
  width: 375px;
}

.form-group {
  margin-bottom: _vertical-rhythm;
}

.mb {
  margin-bottom: _vertical-rhythm;
}

button {
  margin-top: 15px;
  color: _white;
  background: _contrast-color;
  border-radius: 6px;
  cursor: pointer;
  transition: all .3s;
  border: 1px solid transparent;
  padding: 15px 10px;
  text-decoration: none;
  font-size: 18px;
  display: block;
}

button:hover {
  color: _contrast-color;
  background: _white;
  border: 1px solid _contrast-color;
}

.main-container {
  width: 80%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 30px 0;
}

table {
  width: 100%;
  border-collapse: collapse;
}

th, td {
  padding: 15px;
}

th {
  text-align: left;
  border-bottom: 1px solid _black;
}

tr:nth-of-type(even) {
  background: #eee;
}

td.controls {
  display: flex;
  justify-content: space-between;
}

#admin-nav {
  width: 100%;
  background: _black;
  height: 80px;
}

#admin-nav nav {
  width: 100%;
  height: 100%;
}

#admin-nav nav ul {
  width: 100%;
  height: 100%;
  display: flex;
  list-style-type: none;
  justify-content: space-around;
  align-items: center;
}

#admin-nav nav ul a {
  color: white;
}

.tabs {
  display: flex;
  justify-content: space-between;
  margin-bottom: _vertical-rhythm;
  color: _white;
}

.tabs span {
  display: inline-block;
  height: 100%;
  flex-grow: 1;
  text-align: center;
  padding: 15px 0;
  cursor: pointer;
  border: 2px solid _white;
  border-radius: 5px;
  background: _contrast-color;
}

.tabs .active {
  color: _white;
  background: _highlight;
}

.createpost {
  float: right;
  margin-top: 15px;
  color: _white;
  background: _contrast-color;
  border-radius: 6px;
  cursor: pointer;
  transition: all .3s;
  border: 1px solid transparent;
  padding: 15px 10px;
  text-decoration: none;
}

.createpost:hover {
  color: _contrast-color;
  background: _white;
  border: 1px solid _contrast-color;
}

textarea {
  width: 100%;
  height: 500px;
  resize: none;
  font-size: 20px;
}

input {
  display: inline-block;
}

.delete-button {
  display: inline-block;
  padding-top: 11px;
  padding-bottom: 11px;
  margin-top: 0;
  margin-bottom: 30px;
}

.input-container {
  display: flex;
  align-items: center;
}

input {
  margin-bottom: 0;
  margin-right: 10px;
}

.adduser {
  float: right;
  margin-top: 15px;
  color: _white;
  background: _contrast-color;
  border-radius: 6px;
  cursor: pointer;
  transition: all .3s;
  border: 1px solid transparent;
  padding: 15px 10px;
  text-decoration: none;
}

.adduser:hover {
  color: _contrast-color;
  background: _white;
  border: 1px solid _contrast-color;
}

#editor {
  width: 100%;
  margin: 0 auto;
}

.post {
  border-bottom: 1px solid _black;
  margin-bottom: 50px;
}

.comment {
  margin-bottom: _vertical-rhythm;
  border-bottom: 1px solid _black;
}

.outer-container {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  min-height: 100vh;
}

.outer-container .main-container {
  flex-grow: 2;
}

header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 10px 30px;
  border-bottom: 1px solid _black;
  height: 100px;
}

header .branding h1 {
  margin: 0;
  padding: 0;
}

ul {
  list-style-type: none;  
}

ul li {
  display: inline-block;
  margin-left: 30px;
}

footer {
  height: 100px;
  border-top: 1px solid _black;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: center;
}

footer nav ul {
  display: flex;
  width: 600px;
  justify-content: space-between;
  list-style-type: none;
}

footer nav ul li a {
  white-space: nowrap;
}
